/// <reference path="./styles-generated.d.ts" />

interface Style {
  readonly id: StyleId;
  getName(): string;
  getProperties(options?: BreakpointAndPseudo): PropertyMap;
  setProperties(props: PropertyMap, options?: BreakpointAndPseudo): undefined;
  getProperty(
    prop: StyleProperty,
    options?: BreakpointAndPseudo
  ): null | string;
  setProperty(
    prop: StyleProperty,
    value: string,
    options?: BreakpointAndPseudo
  ): undefined;
  clearAllProperties(): undefined;
  save(): Promise<undefined>;
  destroy(): undefined;
}

declare type StyleId = string;

declare type BreakpointAndPseudo = {
  breakpoint?: BreakpointId;
  pseudo?: PseudoStateKey;
};

declare type BreakpointId =
  | 'xxl'
  | 'xl'
  | 'large'
  | 'main'
  | 'medium'
  | 'small'
  | 'tiny';

declare type PseudoStateKey =
  | 'noPseudo'
  | 'nth-child(odd)'
  | 'nth-child(even)'
  | 'first-child'
  | 'last-child'
  | 'hover'
  | 'active'
  | 'pressed'
  | 'visited'
  | 'focus'
  | 'focus-visible'
  | 'focus-within'
  | 'placeholder'
  | 'empty'
  | 'before'
  | 'after';
